import { useState } from "react";

const users = [
  { name: "Jhon", age: 23, Score: 95, Profession: "Frontend Dev" },
  { name: "William", age: 32, Score: 78, Profession: "Backend Dev" },
  { name: "Don Brown", age: 42, Score: 96, Profession: "Full Stack" },
  { name: "Kafka", age: 76, Score: 63, Profession: " System Admin" },
  { name: "Rase", age: 32, Score: 98, Profession: "DevOps " },
  { name: "Alex", age: 22, Score: 23, Profession: "Backend Dev" },
];

const UserSearch: React.FC = () => {
  const [name, setName] = useState("");
  const Search = () => {
    const founduser = users.find((user) => {
      return user.name === name;
    });

    console.log(founduser);
    
  };
  return (
    <div>
      <h4>User Search</h4>
      <input value={name} onChange={(e) => setName(e.target.value)} />
      <button onClick={Search}>Search</button>
    </div>
  );
};
export default UserSearch;
